			from(select rownum rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
    		from(select caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5 from myjeju_cafe order by ca_like desc)) 
    		where rno between #{start} and #{end}

