mapper.select:
		select rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
			from(select rownum rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
    		from(select caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5 from myjeju_cafe order by ca_like desc)) 
    		where rno between #{start} and #{end}

choose.when:
			select rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
				from(select rownum rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
    			from(select caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5 from myjeju_cafe order by ca_like desc)
    			where ca_name like '%'||#{search_text}||'%') 
    			where rno between #{start} and #{end}

			select rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
				from(select rownum rno, caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5
    			from(select caid, ca_name, ca_tag, ca_infor, ca_infor2, ca_addr, ca_vpoint, ca_hpoint, ca_hp, ca_like, ca_image1, ca_image2, ca_image3, ca_image4, ca_image5 from myjeju_cafe order by ca_like desc)
    			where ca_addr like '%'||#{search_text}||'%') 
    			where rno between #{start} and #{end}

mapper.select:
		select rno, reid, id, caid, ca_review, ca_star, ca_time
 		 from(select rownum rno,  reid, id, caid, ca_review, ca_star, ca_time
  		  from(select rownum rno,  reid, id, caid, ca_review, ca_star, to_char(ca_time, 'yyyy-mm-dd') ca_time
   		   from(select reid, id, caid, ca_review, ca_star, ca_time from myjeju_cafe_review order by ca_time) order by rno)
   		    order by rno desc) where caid=#{caid}

		select rno, reid, id, caid, ca_review, ca_star, ca_time
  		 from(select rownum rno, reid, id, caid, ca_review, ca_star, ca_time
 		  from(select rownum rno,  reid, id, caid, ca_review, ca_star, ca_time
  		   from(select rownum rno,  reid, id, caid, ca_review, ca_star, to_char(ca_time, 'yyyy-mm-dd') ca_time
   		    from(select reid, id, caid, ca_review, ca_star, ca_time from myjeju_cafe_review order by ca_time) order by rno)
   		     order by rno desc) where caid=#{caid}) where rno between #{start} and #{end}
